def on_find_button_clicked():
    try:
        word = entry_1.get()
        Urban.setWord(word)
        definition = Urban.definition()
        entry_2.delete("1.0", "end")
        entry_2.insert("1.0", definition)
    except:
        entry_2.delete("1.0", "end")
        entry_2.insert("1.0", "Word not found")

